import React, { useState } from "react";
import DropDownWithSearch from "./DropDownWithSearch.js";
import ReactDatePicker from "react-datepicker";

const CreateView = () => {
  const [formData, setFormData] = useState({
    name: "",
    country: "",
    indicator: "",
    chartType: "",
    startDate: "",
    endDate: "",
  });

  const countries = [
    { value: "India", label: "India" },
    { value: "Russia", label: "Russia" },
  ];

  const handleChange = (name, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can handle the form submission here, e.g., send data to a server, etc.
    console.log("Form data submitted:", formData);
  };

  return (
    <div>
      <h2>Create a View</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name</label>
          <input type="text" name="name" onChange={handleChange} />
        </div>
        <div>
          <label htmlFor="country">Country</label>
          <DropDownWithSearch
            options={countries}
            onChange={({ value }) =>
              setFormData({ ...formData, country: value })
            }
            value={formData.name}
          />
        </div>

        <div>
          <label htmlFor="indicator">Indicator</label>
          <DropDownWithSearch
            options={countries}
            onChange={({ value }) =>
              setFormData({ ...formData, indicator: value })
            }
            value={formData.indicator}
          />
        </div>

        <div>
          <label htmlFor="startDate">Start Date</label>
          <ReactDatePicker
            selected={formData.startDate}
            onChange={(date) => setFormData()}
            dateFormat="dd/MM/yyyy" // Change the date format according to your preference
            placeholderText="Select a date"
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreateView;
